sqlMap.insert:
        
                (SELECT MAX(k_num) + 1 FROM Kepcn_comment);
        

        insert into Kepcn_comment (k_id,k_title,k_newsnum,k_date,icon)
        VALUES (#k_id#, #k_title#, #Rs_num#, #k_date#, #icon#);

insert into rentalsendsms (empno, appno, pre_deptno, now_deptno, hp, regdate, senddate, send_status) select ori_emp.empno ,tmp_emp.appno ,ori_emp.deptno as pre_deptno ,tmp_emp.deptno as now_deptno ,isnull(ori_emp.hp, tmp_emp.hp) as hp ,getdate() ,null ,'0000' from tbSVC_TDMSEMP ori_emp inner join ( select ora.appeno empno ,ora.appno ,te.deptno as deptno ,te.hp from ONESTOP_RENT_APP ora inner join tbSVC_TDMSEMP_tmp te on ora.status = '4' and ora.appeno = te.empno where status = '4' ) as tmp_emp on ori_emp.empno = tmp_emp.empno and ori_emp.deptno != tmp_emp.deptno
union
select ori_emp.empno ,tmp_emp.appno ,ori_emp.deptno as pre_deptno ,tmp_emp.deptno as now_deptno ,isnull(ori_emp.hp, tmp_emp.hp) as hp ,getdate() ,null ,'0000' from tbSVC_TDMSEMP ori_emp inner join ( select oraeu.appueno empno ,ora.appno ,teu.deptno as deptno ,teu.hp from ONESTOP_RENT_APP ora inner join onestop_rent_app_equip_user oraeu on ora.status = '4' and ora.appno = oraeu.appno inner join tbSVC_TDMSEMP_tmp teu on oraeu.appueno = teu.empno where status = '4' ) as tmp_emp on ori_emp.empno = tmp_emp.empno and ori_emp.deptno != tmp_emp.deptno;
delete from tbSVC_TDMSDEPT; insert into tbSVC_TDMSDEPT select * from tbSVC_TDMSDEPT_TMP; delete from tbSVC_TDMSEMP; insert into tbSVC_TDMSEMP select * from tbSVC_TDMSEMP_TMP; delete from tbSVC_TDMSDETAIL; insert into tbSVC_TDMSDETAIL select * from tbSVC_TDMSDETAIL_TMP;
